mapper.select:
select stylistId
from tb_styhouse_stylist
where styHouseId = #{styHouseId};

select COUNT(*)
from tb_styhouse_stylist
where styHouseId = #{styHouseId};

select *
from tb_styhouse_stylist
where styHouseId = #{styHouseId} and status=1;

select count(*)
from tb_styhouse_stylist
where styHouseId = #{styHouseId} and status=1;

select *
from tb_styhouse_stylist
where styHouseId = #{styHouseId} and stylistId = #{stylistId} and status=1;

mapper.update:
update tb_styhouse_stylist
set stylistName=#{stylistName}, stylistPhoto=#{stylistPhoto}, maxNumber=#{maxNumber}, reservationTime=#{reservationTime},stylistIntroduction=#{stylistIntroduction}
where styHouseId=#{styHouseId} and stylistId=#{stylistId};

update tb_styhouse_stylist
set status=0
where styHouseId=#{styHouseId} and stylistId=#{stylistId};
