mapper.select:
SELECT count(*)
FROM tb_user
WHERE userPhone = #{userPhone} and userPassword = #{userPassword} and userStatue = 0;

select userId,userName,userPassword,userPhone,userAge,userSex,userPhoto
from tb_user
where userPhone = #{userPhone} and userPassword = #{userPassword} and userStatue = 0;

SELECT userId
FROM tb_user
WHERE userPhone = #{userPhone};

mapper.update:
UPDATE tb_user
SET userName = #{userName}, userWord = #{userWord}, userSex = #{userSex}
WHERE userId = #{userId};

mapper.select:
SELECT userPhoto, userName, userWord, userSex, userAge
FROM tb_user
WHERE userId = #{userId};
